mapper.update:
update info_ezstop
set ezstop_uid = #{ezstopUid,jdbcType=INTEGER},
  ezstop_air_time = #{ezstopAirTime,jdbcType=TIMESTAMP},
  ezstop_end_time = #{ezstopEndTime,jdbcType=TIMESTAMP},
  "ezstop_parking area code" = #{ezstopParkingAreaCode,jdbcType=VARCHAR}
where ezstop_id = #{ezstopId,jdbcType=INTEGER}

mapper.select:
select ezstop_id, ezstop_uid, ezstop_air_time, ezstop_end_time, "ezstop_parking area code"
from info_ezstop
where ezstop_id = #{ezstopId,jdbcType=INTEGER}

