mapper.update:
update user_admin
set admin_name = #{adminName,jdbcType=VARCHAR},
  admin_tel = #{adminTel,jdbcType=VARCHAR},
  admin_psw = #{adminPsw,jdbcType=VARCHAR}
where admin_id = #{adminId,jdbcType=INTEGER}

mapper.select:
select admin_id, admin_name, admin_tel, admin_psw
from user_admin
where admin_id = #{adminId,jdbcType=INTEGER}
